package ComputerProgrammingPartOne;

public class Program3_9 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = 9;

		if (number % 2 == 0) {
			System.out.printf("The number is even\n");
		} else {
			System.out.printf("The number is odd\n");
		}

	}

}
